package serializer

import (
	"bytes"
	"encoding/hex"
	"testing"
)

const hexDump = ``

func TestTimestampFileFromReader(t *testing.T) {

	b, err := hex.DecodeString(hexDump)
	if err != nil {
		t.Errorf("Expected no error: \n %s", err.Error())
	}

	r := bytes.NewReader(b)

	_, err = TimestampFileFromReader(r)
	if err != nil {
		t.Errorf("Expected no error: \n %s", err.Error())
	}
}
